options.systemMessage:
=You are specialized in generating url array outputs out of xml based sitemaps,

please output in the following format:
{
urls: ["www.url1.com"]
}

parameters.jsonBody:
={
  "urls": "https://ai.google.dev/gemini-api/docs/pricing",
  "crawler_params": {
    "headless": true
  },
  "extraction_config": {
    "type": "llm",
    "params": {
      "llm_config": {
        "provider": "openai/gpt4o-mini"
      },
      "schema": {
        "title": "GeminiModelFee",
        "type": "object",
        "properties": {
          "model_name": {
            "title": "Model Name",
            "description": "Name of the Gemini model.",
            "type": "string"
          },
          "input_fee": {
            "title": "Input Fee",
            "description": "Fee for input token for the Gemini model.",
            "type": "string"
          },
          "output_fee": {
            "title": "Output Fee",
            "description": "Fee for output token for the Gemini model.",
            "type": "string"
          }
        },
        "required": ["model_name", "input_fee", "output_fee"]
      },
      "extraction_type": "schema",
      "instruction": "From the crawled content, extract all mentioned model names along with their fees for input and output tokens. Do not miss any models in the entire content."
    }
  },
  "cache_mode": "bypass"
}

={
  "urls": {{ JSON.stringify(Array.from({ length: 50 }, (_, i) => `https://sandbox.oxylabs.io/products?page=${i + 1}`)) }},
  "crawler_params": {
    "headless": true
  },
  "extraction_config": {
    "type": "json_css",
    "params": {
      "schema": {
        "name": "Oxylabs Products",
        "baseSelector": "div.product-card",
        "fields": [
          {
            "name": "product_link",
            "selector": ".card-header",
            "type": "attribute",
            "attribute": "href"
          },
          {
            "name": "title",
            "selector": ".title",
            "type": "text"
          },
          {
            "name": "rating",
            "type": "list",
            "selector": ".rating svg",
            "fields": [
              {
                "name": "star_svg",
                "type": "html"
              }
            ]
          },
          {
            "name": "description",
            "selector": ".description",
            "type": "text"
          },
          {
            "name": "price",
            "selector": ".price-wrapper",
            "type": "text"
          },
          {
            "name": "stock_status",
            "selector": ".in-stock",
            "type": "text"
          }
        ]
      },
      "verbose": true
    }
  },
  "cache_mode": "bypass",
  "semaphore_count": 2
}

parameters.jsonOutput:
={
  "products": {{ 
    $('Get CSS Crawl').last().json.results.flatMap(item => {
      const parsed = JSON.parse(item.extracted_content);
      return parsed.map(({ rating, ...rest }) => ({
        ...rest,
        star_count: Array.isArray(rating) ? rating.length : 0
      }));
    })
  }}
}
